<?php	global $wp_customize;	$zerif_bigtitle_show = get_theme_mod('zerif_bigtitle_show');		if( isset($zerif_bigtitle_show) && $zerif_bigtitle_show != 1 ):			echo '<div class="header-content-wrap">';		elseif ( isset( $wp_customize ) ):			echo '<div class="header-content-wrap zerif_hidden_if_not_customizer">';		endif;	if( (isset($zerif_bigtitle_show) && $zerif_bigtitle_show != 1) || isset( $wp_customize ) ):		echo '<div class="container big-title-container">';		/* Big title */				$zerif_bigtitle_title = get_theme_mod( 'zerif_bigtitle_title','To add a title here please go to Customizer, "Big title section"' );				if( !empty($zerif_bigtitle_title) ):			echo '<h1 class="intro-text">'.$zerif_bigtitle_title.'</h1>';					elseif ( isset( $wp_customize ) ):					echo '<h1 class="intro-text zerif_hidden_if_not_customizer"></h1>';		endif;			/* Buttons */				$zerif_bigtitle_redbutton_label = get_theme_mod( 'zerif_bigtitle_redbutton_label','One button' );		$zerif_bigtitle_redbutton_url = get_theme_mod( 'zerif_bigtitle_redbutton_url','#' );		$zerif_bigtitle_greenbutton_label = get_theme_mod( 'zerif_bigtitle_greenbutton_label','Another button' );		$zerif_bigtitle_greenbutton_url = get_theme_mod( 'zerif_bigtitle_greenbutton_url','#' );		if( (!empty($zerif_bigtitle_redbutton_label) && !empty($zerif_bigtitle_redbutton_url)) ||		(!empty($zerif_bigtitle_greenbutton_label) && !empty($zerif_bigtitle_greenbutton_url))):			echo '<div class="buttons">';				/* Red button */							if (!empty($zerif_bigtitle_redbutton_label) && !empty($zerif_bigtitle_redbutton_url)):					echo '<a href="'.$zerif_bigtitle_redbutton_url.'" class="btn btn-primary custom-button red-btn">'.$zerif_bigtitle_redbutton_label.'</a>';									elseif ( isset( $wp_customize ) ):					echo '<a href="" class="btn btn-primary custom-button red-btn zerif_hidden_if_not_customizer"></a>';				endif;				/* Green button */				if (!empty($zerif_bigtitle_greenbutton_label) && !empty($zerif_bigtitle_greenbutton_url)):					echo '<a href="'.$zerif_bigtitle_greenbutton_url.'" class="btn btn-primary custom-button green-btn">'.$zerif_bigtitle_greenbutton_label.'</a>';				elseif ( isset( $wp_customize ) ):					echo '<a href="" class="btn btn-primary custom-button green-btn zerif_hidden_if_not_customizer"></a>';				endif;			echo '</div>';		endif;		echo '</div>';	echo '</div><!-- .header-content-wrap -->';		endif;	echo '<div class="clear"></div>';?>